var sk = new Object();
sk.noSave = function () {
  window.onbeforeunload = function () {
    return true;
  };
};

sk.loginOut = function () {
  $.ajax({
    url: "/api/loginOut",
    method: "POST",
    success: function (data) {
      if (data.code == 1000) {
        layer.msg("登出成功！");
        sleep(1000);
        window.location.href = "/admin";
      }
    },
  });
};
